tests.testCode:
function testAppTitle() {
    const title = document.getElementById('app-title');
    if (!title) {
        return { passed: false, message: 'h1 element with id="app-title" not found' };
    }
    if (title.textContent.trim() !== 'Todo List') {
        return { passed: false, message: 'App title should contain "Todo List"' };
    }
    return { passed: true, message: 'App title exists and is correct' };
}

function testTodoInput() {
    const input = document.getElementById('todo-input');
    if (!input) {
        return { passed: false, message: 'Input element with id="todo-input" not found' };
    }
    if (input.placeholder !== 'Enter a new todo...') {
        return { passed: false, message: 'Input placeholder should be "Enter a new todo..."' };
    }
    return { passed: true, message: 'Todo input field exists with correct placeholder' };
}

function testAddButton() {
    const button = document.getElementById('add-btn');
    if (!button) {
        return { passed: false, message: 'Button element with id="add-btn" not found' };
    }
    if (button.textContent.trim() !== 'Add Todo') {
        return { passed: false, message: 'Button text should be "Add Todo"' };
    }
    return { passed: true, message: 'Add button exists with correct text' };
}

function testTodoListContainer() {
    const container = document.getElementById('todo-list');
    if (!container) {
        return { passed: false, message: 'Div element with id="todo-list" not found' };
    }
    return { passed: true, message: 'Todo list container exists' };
}

boilerplateCode.styles.css:
body {
    font-family: Arial, sans-serif;
    max-width: 600px;
    margin: 0 auto;
    padding: 20px;
}

#app-title {
    text-align: center;
    color: #333;
}

#todo-input, #search-input {
    width: 70%;
    padding: 10px;
    margin: 10px 0;
    border: 1px solid #ddd;
    border-radius: 4px;
}

#add-btn {
    width: 25%;
    padding: 10px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

.todo-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    margin: 5px 0;
    border: 1px solid #eee;
    border-radius: 4px;
}

.delete-btn {
    background-color: #dc3545;
    color: white;
    border: none;
    padding: 5px 10px;
    border-radius: 3px;
    cursor: pointer;
}

