function testAddTodo() {
    const input = document.getElementById('todo-input');
    const addBtn = document.getElementById('add-btn');
    const todoList = document.getElementById('todo-list');

    // Clear any existing todos
    todoList.innerHTML = '';

    // Add a test todo
    input.value = 'Test Todo Item';
    addBtn.click();

    const todoItems = todoList.querySelectorAll('.todo-item');
    if (todoItems.length === 0) {
        return { passed: false, message: 'No todo item was created when add button was clicked' };
    }

    const todoText = todoItems[0].querySelector('.todo-text');
    if (!todoText) {
        return { passed: false, message: 'Todo item should contain an element with class="todo-text"' };
    }

    if (todoText.textContent.trim() !== 'Test Todo Item') {
        return { passed: false, message: 'Todo text should match the input value' };
    }

    return { passed: true, message: 'Todo item created successfully' };
}

function testInputClears() {
    const input = document.getElementById('todo-input');
    const addBtn = document.getElementById('add-btn');

    input.value = 'Another Test Todo';
    addBtn.click();

    if (input.value !== '') {
        return { passed: false, message: 'Input field should be cleared after adding a todo' };
    }

    return { passed: true, message: 'Input field cleared successfully' };
}

function testDeleteButton() {
    const input = document.getElementById('todo-input');
    const addBtn = document.getElementById('add-btn');
    const todoList = document.getElementById('todo-list');

    todoList.innerHTML = '';
    input.value = 'Todo with Delete Button';
    addBtn.click();

    const todoItem = todoList.querySelector('.todo-item');
    if (!todoItem) {
        return { passed: false, message: 'No todo item found' };
    }

    const deleteBtn = todoItem.querySelector('.delete-btn');
    if (!deleteBtn) {
        return { passed: false, message: 'Todo item should contain a delete button with class="delete-btn"' };
    }

    return { passed: true, message: 'Delete button exists in todo item' };
}

