function testDeleteTodo() {
    const input = document.getElementById('todo-input');
    const addBtn = document.getElementById('add-btn');
    const todoList = document.getElementById('todo-list');

    // Clear and add test todos
    todoList.innerHTML = '';
    if (window.todos) window.todos = [];

    input.value = 'Todo to Delete';
    addBtn.click();
    input.value = 'Todo to Keep';
    addBtn.click();

    let todoItems = todoList.querySelectorAll('.todo-item');
    if (todoItems.length !== 2) {
        return { passed: false, message: 'Should have 2 todo items before deletion' };
    }

    // Click delete button on first item
    const firstDeleteBtn = todoItems[0].querySelector('.delete-btn');
    if (!firstDeleteBtn) {
        return { passed: false, message: 'Delete button not found' };
    }

    firstDeleteBtn.click();

    todoItems = todoList.querySelectorAll('.todo-item');
    if (todoItems.length !== 1) {
        return { passed: false, message: 'Todo item was not deleted from DOM' };
    }

    const remainingText = todoItems[0].querySelector('.todo-text').textContent;
    if (remainingText !== 'Todo to Keep') {
        return { passed: false, message: 'Wrong todo item was deleted' };
    }

    return { passed: true, message: 'Todo deleted successfully' };
}

