function testSearchFilter() {
    const input = document.getElementById('todo-input');
    const addBtn = document.getElementById('add-btn');
    const searchInput = document.getElementById('search-input');
    const todoList = document.getElementById('todo-list');

    // Clear and add test todos
    todoList.innerHTML = '';
    if (window.todos) window.todos = [];

    input.value = 'Buy groceries';
    addBtn.click();
    input.value = 'Walk the dog';
    addBtn.click();
    input.value = 'Buy books';
    addBtn.click();

    // Search for 'buy'
    searchInput.value = 'buy';
    searchInput.dispatchEvent(new Event('input'));

    setTimeout(() => {
        const visibleTodos = todoList.querySelectorAll('.todo-item:not(.hidden)');
        const allTodos = todoList.querySelectorAll('.todo-item');

        if (allTodos.length < 3) {
            return { passed: false, message: 'Not all todos were added' };
        }

        // Should show 2 todos containing 'buy'
        let visibleCount = 0;
        allTodos.forEach(todo => {
            const text = todo.querySelector('.todo-text').textContent.toLowerCase();
            if (text.includes('buy')) {
                if (todo.style.display === 'none' || todo.classList.contains('hidden')) {
                    return { passed: false, message: 'Todo containing "buy" should be visible' };
                }
                visibleCount++;
            } else {
                if (todo.style.display !== 'none' && !todo.classList.contains('hidden')) {
                    return { passed: false, message: 'Todo not containing "buy" should be hidden' };
                }
            }
        });

        if (visibleCount !== 2) {
            return { passed: false, message: `Expected 2 visible todos, got ${visibleCount}` };
        }

        return { passed: true, message: 'Search filter works correctly' };
    }, 100);

    return { passed: true, message: 'Search test initiated' };
}

function testClearSearch() {
    const searchInput = document.getElementById('search-input');
    const clearBtn = document.getElementById('clear-search-btn');
    const todoList = document.getElementById('todo-list');

    // First set a search term
    searchInput.value = 'test';
    searchInput.dispatchEvent(new Event('input'));

    // Then clear it
    if (clearBtn) {
        clearBtn.click();
    } else {
        searchInput.value = '';
        searchInput.dispatchEvent(new Event('input'));
    }

    setTimeout(() => {
        const allTodos = todoList.querySelectorAll('.todo-item');
        let hiddenCount = 0;

        allTodos.forEach(todo => {
            if (todo.style.display === 'none' || todo.classList.contains('hidden')) {
                hiddenCount++;
            }
        });

        if (hiddenCount > 0) {
            return { passed: false, message: 'All todos should be visible when search is cleared' };
        }

        return { passed: true, message: 'Clear search works correctly' };
    }, 100);

    return { passed: true, message: 'Clear search test initiated' };
}
